import styled from "styled-components";
import { typeScale } from "../utils";
import { applyStyleModifiers } from "styled-components-modifiers";

const BUTTON_MODIFIERS = {
  small: () => `
    font-size: ${typeScale.helperText};
    padding: 8px;
  `,
  large: () => `
    font-size: ${typeScale.header5};
    padding: 16px 24px;
  `,
  warning: () => `
    background: none;
    color: ${props => props.theme.status.warningColor};
    &:hover, &:focus {
      background-color: ${props => props.theme.status.warningColorHover};
      outline: 3px solid ${props => props.theme.status.warningColorHover};
      outline-offset: 2px;
      border: 2px solid transparent;
    }

    &:active {
      background-color: ${props => props.status.warningColorActive};
    }
  `,
  primaryButtonWarning: () => `
    background-color: ${props => props.theme.status.warningColor};
    color: ${props => props.theme.textColorInverted};
  `,
  secondaryButtonWarning: () => `
    border: 2px solid ${props => props.theme.status.warningColor};
  `,
  error: () => `
    background: none;
    color: ${props => props.theme.status.errorColor};
    &:hover, &:focus {
      background-color: ${props => props.theme.status.errorColorHover};
      outline: 3px solid ${props => props.theme.status.errorColorHover};
      outline-offset: 2px;
      border: 2px solid transparent;
    }
    &:active {
      background-color: ${props => props.theme.status.errorColorActive};
    }
  `,
  primaryButtonError: () => `
    background-color: ${props .status.errorColor};
    color: ${props .textColorInverted};
  `,
  secondaryButtonError: () => `
    border: 2px solid ${props .status.warningColor};
  `,
  success: () => `
    background: none;
    color: ${props .status.successColor};
    &:hover, &:focus {
      background-color: ${props .status.successColorHover};
      outline: 3px solid ${props .status.successColorHover};
      outline-offset: 2px;
      border: 2px solid transparent;
    }
    &:active {
      background-color: ${props .status.successColorActive};
    }
  `,
  primaryButtonSuccess: () => `
    background-color: ${props .status.successColor};
    color: ${props .textColorInverted};
  `,
  secondaryButtonSuccess: () => `
    border: 2px solid ${props .status.warningColor};
  `,
};

const Button = styled.button`
  padding: 8px 12px;
  font-size: ${typeScale.paragraph};
  border-radius: 2px;
  min-width: 100px;
  cursor: pointer;
  font-family: ${props .primaryFont};
  border-radius: 19px;
  color: white;
  transition: background-color 0.2s linear, color 0.2s linear;

  &:hover {
    background-color: ${props .primaryHoverColor};
    color: ${props .textColorOnPrimary};
  }
  &:focus {
    outline: 3px solid ${props .primaryHoverColor};
    outline-offset: 2px;
  }

  &:active {
    background-color: ${props .primaryActiveColor};
    border-color: ${props .primaryActiveColor};
    color: ${props .textColorOnPrimary};
  }
  &:disabled {
    cursor: not-allowed;
    background: none;
    color: ${props .disabled};
    border: none;
  }
`;

export const PrimaryButton = styled(Button)`
  background-color: ${props .primaryColor};
  color: ${props .textColorOnPrimary};
  border: 2px solid transparent;

  &:disabled {
    background-color: ${props .disabled};
    color: ${props .textOnDisabled};
    border-color: ${props .disabled};
  }
  ${applyStyleModifiers(BUTTON_MODIFIERS)}
`;

export const SecondaryButton = styled(Button)`
  border: 1px solid ${props .primaryColor};
  background-color: transparent;
  color: ${props .textColor};

  &:disabled {
    border: 2px solid ${props .disabled};
  }
  ${applyStyleModifiers(BUTTON_MODIFIERS)}
`;

export const TertiaryButton = styled(Button)`
  border: 2px solid transparent;
  background-color: transparent;
  color: ${props .textColor};
  ${applyStyleModifiers(BUTTON_MODIFIERS)}
`;
