import React, { useState } from "react";
import ReactDOM from "react-dom";
import {
  PrimaryButton,
  SecondaryButton,
  TertiaryButton,
} from "./components/Buttons";
import { ThemeProvider } from 'styled-components';
import { GlobalStyle, darkTheme, defaultTheme } from "./utils";

const App = () => {
  const [useDarkTheme, setUseDarkTheme] = useState(false)
  return (
    <>
      <ThemeProvider theme={useDarkTheme ? darkTheme : defaultTheme}>
        <button
          style={{
            margin: "0 16px 24px",
            padding: "8px",
            background: "none",
            cursor: "pointer",
            border: "2px solid #000",
            marginTop: "60px",
          }}
          onClick={() => setUseDarkTheme(true)}
        >
          Dark theme
        </button>
        <button
          style={{
            margin: "0 16px 24px",
            padding: "8px",
            background: "none",
            cursor: "pointer",
            border: "2px solid #000",
            marginTop: "60px",
          }}
          onClick={() => setUseDarkTheme(false)}
        >
          Default theme
        </button>
          <div
        style={{
          background: useDarkTheme
            ? defaultTheme.primaryBackground
            : darkTheme.primaryBackground,
          width: "100vw",
          height: "90vh",
          display: "flex",
          alignItems: "center",
          justifyContent: "space-around"
        }}
      >
        <PrimaryButton modifiers={["small", "success", "primaryButtonSuccess"]}>
          Primary Button
        </PrimaryButton>
        <SecondaryButton
          modifiers={["large", "warning", "secondaryButtonWarning"]}
        >
          Secondary Button
        </SecondaryButton>
        <TertiaryButton modifiers={["error", "tertiaryButtonError"]}>
          Tertiary Button
        </TertiaryButton>
        <GlobalStyle />
        </div>
      </ThemeProvider>
    </>
  );
};

ReactDOM.render(<App />, document.querySelector("#root"));
