import styled from "styled-components";

export const primaryGreen = "#4B5229";

const Button = styled.button`
  padding: 8px 12px;
  font-size: 1rem;
  border-radius: 2px;
  min-width: 100px;
  cursor: pointer;
  font-family: "Indie Flower", cursive;
`;

const PrimaryButton = styled(Button)`
  background-color: #4B5229;
  border: none;
  color: white;
`;
